import React, { useState } from 'react';
import './App.css';
import SearchModal from './components/SearchModal';

function App() {
  const [isSearchOpen, setIsSearchOpen] = useState(false);

  const openSearch = () => {
    setIsSearchOpen(true);
  };

  const closeSearch = () => {
    setIsSearchOpen(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>🎬 영화 추천 프로그램</h1>
        <p>당신에게 맞는 영화를 찾아보세요!</p>
        
        <button 
          className="search-button"
          onClick={openSearch}
        >
          🔍 영화 검색하기
        </button>
      </header>

      <main className="App-main">
        <div className="features">
          <div className="feature-card">
            <h3>🎯 맞춤 추천</h3>
            <p>당신의 취향에 맞는 영화를 추천해드립니다</p>
          </div>
          <div className="feature-card">
            <h3>📊 상세 정보</h3>
            <p>평점, 리뷰, 출연진 등 상세한 영화 정보를 제공합니다</p>
          </div>
          <div className="feature-card">
            <h3>💡 새로운 발견</h3>
            <p>평소 접하지 못했던 숨겨진 명작들을 발견하세요</p>
          </div>
        </div>
      </main>

      <SearchModal 
        isOpen={isSearchOpen} 
        onClose={closeSearch} 
      />
    </div>
  );
}

export default App;
